import React, {useState, useEffect } from 'react'; 
import axios from 'axios'; 
import {Link, useNavigate} from 'react-router-dom'; 


const AllRequests = () => {

    const [requestList, setRequestList] = useState([]); 
    const navigate = useNavigate(); 

    useEffect(()=>{
        axios.get('http://localhost:8000/api/callbells')
        .then((res)=>{
            setRequestList(res.data)
        }).catch((err)=>{
            console.log(err)
        })
    }, [])

    const deleteHandler =(id)=>{
        axios.delete(`http://localhost:8000/api/callbells/${id}`)
        .then((res)=>{
            const newRequestList = requestList.filter((request)=>{
                return request._id !== id
            })
            setRequestList(newRequestList)
            navigate('/')
        }).catch((err)=>{
            console.log(err)
        })
    }
    return (
        <div> 
            <div className="nav-bar">
                <a className="nav-bar" target="_blank" rel="noopener noreferrer" href="https://www.linkedin.com/in/nancy-do-9588a9158/">Contact the Creator</a> |                    
                <a className="nav-bar" target="_blank" rel="noopener noreferrer" href="https://github.com/nancydo272">About the Creator </a> |
                <a className="nav-bar" target="_blank" rel="noopener noreferrer" href="https://github.com/nancydo272/5WestProject">About This Project</a> 
            </div>
            <h1>Welcome to 5W/NW at TJUH Center City!</h1>
            <div className="main-container">
                <table className="col-12 mx-auto table table-hover text-start mt-4">
                    <thead>
                        <tr>
                            <th>Room #</th>
                            <th>Request</th>
                            <th>Urgency</th>
                            <th>Actions</th>
                        </tr>
                    </thead>

                    {
                        requestList.map((request)=>(
                        <tr>
                            <td className="room-num">{request.room}</td>
                            <td>{request.request} {request.custom}</td>
                            <td>{request.urgency}</td>
                            <td>
                                <Link to={`/requests/${request._id}`}>Details</Link>  |
                                <button className="btn btn-link" onClick={()=>deleteHandler(request._id)}>Completed</button>
                            </td>
                        </tr>
                    ))
                }
                </table>
                <Link to={`/requests/new`}>Add A New Request</Link>
            </div>
        </div>
    )
}

export default AllRequests
