import React, {useEffect, useState} from 'react'; 
import axios from 'axios'; 
import {useParams, Link, useNavigate} from 'react-router-dom'; 

const OneRequest = () => {

    const {id} = useParams(); 
    const [request, setRequest] = useState(''); 
    const [requestList, setRequestList] = useState([]); 
    const navigate = useNavigate(); 

    useEffect(()=>{
        axios.get(`http://localhost:8000/api/callbells/${id}`)
        .then((res)=>{
            setRequest(res.data)
        }).catch((err)=>{
            console.log(err)
        })
    }, [])

    const deleteHandler =(id)=>{
        axios.delete(`http://localhost:8000/api/callbells/${id}`)
        .then((res)=>{
            const newRequestList = requestList.filter((request)=>{
                return request._id !== id
            })
            setRequestList(newRequestList)
            navigate('/unitDashboard')
        }).catch((err)=>{
            console.log(err)
        })
    }

    return (
        <div>
            <div className="d-flex justify-content-around">
                <h1>{request.room}'s Request</h1>
                <Link to ={'/unitDashboard'}>back to home</Link>
            </div>
            <div className="d-flex justify-content-center">
                <h5>Request:</h5>
                <p>{request.request} {request.custom} </p>
            </div>
            <div className="d-flex justify-content-center">
                <h5>Urgency:</h5>
                <p>{request.urgency}</p>
            </div>
            <div className="d-flex justify-content-center">
                <button className="btn btn-primary" onClick={()=>deleteHandler(request._id)} >Completed</button>
                <Link to={`/requests/${request._id}/edit`}>Edit</Link>
            </div>
        </div>
    )
}

export default OneRequest
